*,
*::before,
*::after {
  box-sizing: border-box;
}

/* Custom Properties, update these for your own design */

:root {
  --ff-primary: 'Source Sans Pro', sans-serif;
  --ff-secondary: 'Source Code Pro', monospace;

  --fw-reg: 300;
  --fw-bold: 900;

  --clr-light: #fff;
  --clr-dark: #303030;
  --clr-accent: #4285f4;

  --fs-h1: 3rem;
  --fs-h2: 2.25rem;
  --fs-h3: 1.25rem;
  --fs-body: 1rem;

  --bs: 0.25em 0.25em 0.75em rgba(0, 0, 0, 0.25),
    0.125em 0.125em 0.25em rgba(0, 0, 0, 0.15);
}

@media (min-width: 800px) {
  :root {
    --fs-h1: 4.5rem;
    --fs-h2: 3.75rem;
    --fs-h3: 1.5rem;
    --fs-body: 1.125rem;
  }
}

/* General styles */

html {
  scroll-behavior: smooth;
}

body {
  background: var(--clr-light);
  color: var(--clr-dark);
  margin: 0;
  font-family: var(--ff-primary);
  font-size: var(--fs-body);
  line-height: 1.6;
}

section {
  padding: 1em 2em;
}

img {
  max-width: 100%;
}

:focus {
  outline: 3px solid var(--clr-accent);
  outline-offset: 3px;
}

/* buttons */

.btn {
  display: inline-block;
  padding: 0.5em 2.5em;
  background: var(--clr-accent);
  color: var(--clr-dark);
  text-decoration: none;
  cursor: pointer;
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 2px;
  font-weight: var(--fw-bold);
  transition: transform 200ms ease-in-out;
}

.btn:hover {
  transform: scale(1.1);
}

/* Typography */

h1,
h2,
h3 {
  line-height: 1;
  margin: 0;
}

h1 {
  font-size: var(--fs-h1);
}
h2 {
  font-size: var(--fs-h2);
}
h3 {
  font-size: var(--fs-h3);
}

.section__title {
  margin-bottom: 0.25em;
}

.section__title--intro {
  font-weight: var(--fw-reg);
}

.section__title--intro strong {
  display: block;
  font-weight: var(--fw-bold);
}

.section__subtitle {
  margin: 0;
  font-size: var(--fs-h3);
}

.section__subtitle--intro,
.section__subtitle--about {
  background: var(--clr-accent);
  padding: 0.25em 1em;
  font-family: var(--ff-secondary);
  margin-bottom: 1em;
}

/* header */

.header {
  display: flex;
  justify-content: space-between;
}

.language {
  display: flex;
  padding: 10px 20px;
}

.selected {
  font-weight: var(--fw-bold);
  cursor: default;
}

.language a {
  color: inherit;
  cursor: pointer;
}

.logo {
  display: flex;
  justify-content: space-between;
  padding: unset;
}

.logo-img {
  width: 200px;
  -o-object-fit: contain;
  object-fit: contain;
}

.nav {
  position: fixed;
  background: var(--clr-dark);
  color: var(--clr-light);
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: 100;

  transform: translateX(100%);
  transition: transform 250ms cubic-bezier(0.5, 0, 0.5, 1);
}

.nav__list {
  list-style: none;
  display: flex;
  height: 100%;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
  margin: 0;
  padding: 0;
}

.nav__link {
  color: inherit;
  font-weight: var(--fw-bold);
  font-size: var(--fs-h2);
  text-decoration: none;
}

.nav__link:hover {
  color: var(--clr-accent);
}

.nav-open .nav-toggle {
  position: fixed;
}

.nav-open .hamburger {
  transform: rotate(0.625turn);
}

.nav-open .hamburger::before {
  transform: rotate(90deg) translateX(-6px);
}

.nav-open .hamburger::after {
  opacity: 0;
}

.nav-toggle {
  padding: 0.5em;
  background: transparent;
  border: 0;
  cursor: pointer;
  position: absolute;
  right: 1em;
  top: 1em;
  z-index: 1000;
}

.nav-open .nav {
  transform: translateX(0%);
}

.hamburger {
  display: block;
  position: relative;
}

.hamburger,
.hamburger::before,
.hamburger::after {
  background: var(--clr-accent);
  width: 2em;
  height: 3px;
  border-radius: 1em;
  transition: transform 250ms ease-in-out;
}

.hamburger::before,
.hamburger::after {
  content: '';
  position: absolute;
  left: 0;
  right: 0;
}

.hamburger::before {
  top: 6px;
}
.hamburger::after {
  bottom: 6px;
}

@media screen and (min-width: 768px) {
  .header {
    padding: 0;
    display: flex;
  }
  .logo {
    padding: 1em;
    display: flex;
  }
  section {
    padding: 5em 2em;
  }

  .nav-toggle {
    display: none;
  }
  .nav {
    position: relative;
    transform: translateX(0%);
    width: 100%;
  }
  .nav__list {
    flex-direction: row;
  }
  .nav__item {
    --fs-h2: 1.5rem;
    margin-left: 1.5rem;
    margin-right: 1.5rem;
  }
}

/*  Intro section  */

.intro {
  position: relative;
}

.intro__img {
  box-shadow: var(--bs);
}

@media (min-width: 600px) {
  .intro {
    display: grid;
    width: -webkit-min-content;
    width: -moz-min-content;
    width: min-content;
    margin: 0 auto;
    grid-column-gap: 1em;
    grid-template-areas:
      'img title'
      'img subtitle';
    grid-template-columns: -webkit-min-content -webkit-max-content;
    grid-template-columns: min-content max-content;
  }

  .intro__img {
    grid-area: img;
    min-width: 250px;
    position: relative;
    z-index: 2;
  }

  .section__subtitle--intro {
    align-self: start;
    grid-column: -1 / 1;
    grid-row: 2;
    text-align: right;
    position: relative;
    left: -1.5em;
    width: calc(100% + 1.5em);
  }
}

/* Experincia */

.my-services {
  background-color: var(--clr-dark);
  background-image: url(./Images/pc3.png);
  background-size: cover;
  -o-object-fit: cover;
  object-fit: cover;
  background-blend-mode: multiply;
  color: var(--clr-light);
  text-align: center;
}

.section__title--services {
  color: var(--clr-accent);
  position: relative;
}

.section__title--services::after {
  content: '';
  display: block;
  width: 3em;
  height: 1px;
  margin: 0.5em auto 1em;
  background: var(--clr-light);
  opacity: 0.5;
}

.services {
  margin-bottom: 4em;
}

.service {
  max-width: 500px;
  margin: 0 auto;
}

@media (min-width: 800px) {
  .services {
    display: flex;
    max-width: 1000px;
    margin-left: auto;
    margin-right: auto;
  }

  .service + .service {
    margin-left: 2em;
  }
}

/* / Experincia */

/* Sobre mim */

.about-me {
  max-width: 1000px;
  margin: 0 auto;
}

.about-me__img {
  box-shadow: var(--bs);
}

/* / Sobre mim */

/* Projects */

.my-work {
  background-color: var(--clr-dark);
  color: var(--clr-light);
}

.section__title--work {
  color: var(--clr-accent);
  margin-bottom: 3rem;
}

.section__subtitle--work {
  background-color: var(--clr-dark);
  color: var(--clr-light);
  text-align: center;
}

.portfolio {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  grid-column-gap: 5rem;
  justify-content: center;
}

.portfolio__item {
  text-decoration: none;
  box-shadow: var(--bs);
  padding-bottom: 1rem;
  margin-bottom: 1rem;
}
.portfolio__item img {
  cursor: pointer;
}
.tech {
  display: none;
}

.container-projects {
  display: flex;
  justify-content: space-evenly;
  text-align: center;
}

.project-title {
  text-decoration: none;
  color: var(--clr-light);
  font-size: 1.5em;
  margin-bottom: 0rem;
  margin-top: 1rem;
}

.btn-project {
  border-radius: 10px;
  margin-top: 10px;
  background-color: #4285f4;
  color: var(--clr-light);
  cursor: pointer;
  font-size: 0.5rem;
}
.btn-project:hover {
  opacity: 0.8;
}
/* modal */

.work-modal {
  display: none;
}

.show-modal {
  position: fixed;
  background: rgba(0, 0, 0, 0.5);
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  z-index: 999;
  display: flex;
  justify-content: center;
  align-items: center;
}
.work-modal-item {
  display: flex;
  height: auto;
  width: 80%;
  border: 0.1px solid var(--clr-accent);
  border-radius: 0.4rem;
  background-color: var(--clr-dark);
}

.project__left {
  display: flex;
  flex-direction: column;
  justify-content: center;
}

.project__about {
  display: flex;
  flex-direction: column;
  background-color: #343a41;
}

.tech__resume {
  padding: 10px;
  text-align: left;
}
.tech__resume p {
  overflow-y: scroll;
  max-height: 150px;
  font-size: 18px;
}
/* Scroll Bar */
#style::-webkit-scrollbar {
  width: 6px;
  background-color: #f5f5f5;
  border-radius: 10px;
}
#style::-webkit-scrollbar-track {
  box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
  border-radius: 10px;
  background-color: var(--clr-dark);
}
#style::-webkit-scrollbar-thumb {
  border-radius: 10px;
  box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
  background-color: var(--clr-accent);
}

#tech__id::-webkit-scrollbar {
  width: 6px;
  background-color: #f5f5f5;
  border-radius: 10px;
}
#tech__id::-webkit-scrollbar-track {
  box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
  border-radius: 10px;
  background-color: var(--clr-dark);
}
#tech__id::-webkit-scrollbar-thumb {
  border-radius: 10px;
  box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
  background-color: var(--clr-accent);
}
/* Scroll Bar */

.techs {
  margin-top: 20px;
  display: flex;
  flex-wrap: wrap;
  max-height: 100px;
  overflow-y: scroll;
}

.techs p {
  /* display: inline-block; */
  border: 0.1px solid var(--clr-dark);
  border-radius: 0.2em;
  font-size: 0.8em;
  padding: 0.2rem 0.3rem;
  font-weight: lighter;
  margin: 0.3rem;
}

@media screen and (min-width: 768px) {
  .work-modal-item {
    display: flex;
    width: auto;
    height: 400px;
    background-color: var(--clr-dark);
    overflow: auto;
  }

  .project__about {
    max-width: 250px;
  }

  .work-modal-item img {
    -o-object-fit: contain;
    object-fit: contain;
    width: 500px;
  }

  .techs {
    overflow-y: unset;
  }

  .btn-project {
    border-radius: 10px;
    margin-top: 10px;
    background-color: #4285f4;
    color: var(--clr-light);
    cursor: pointer;
    font-size: unset;
  }
}
.work-modal-close {
  font-size: 1.5rem;
  color: var(--clr-accent);
  transition: color 0.5s linear;
  cursor: pointer;
  text-decoration: none;
  margin-left: auto;
  padding: 0 10px;
}
.work-modal-close:hover {
  opacity: 0.8;
}

/* / modal */

/* / Projects */
/* footer */

.footer {
  background: #111;
  color: var(--clr-accent);
  text-align: center;
  padding: 2.5em 0;
  font-size: var(--fs-h3);
}

.footer a {
  color: inherit;
  text-decoration: none;
}

.footer__link {
  font-weight: var(--fw-bold);
}

.footer__link:hover,
.social-list__link:hover {
  opacity: 0.7;
}

.social-list {
  list-style: none;
  display: flex;
  justify-content: center;
  margin: 2em 1em 0;
  padding: 0;
}

.social-list__item {
  margin: 0 0.5em;
}

.social-list__link {
  padding: 0.5em;
}
